<?phpfunction __autoload($classname) {    include_once("controller/$classname.php");}$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['action']) {    case 'articles':        $controller = new C_Page();        break;    default:        $controller = new C_Page();}$controller->Request($action);